declare
v_tipopedd varchar2(50);
v_tipopedd2 varchar2(50);
v_tipopedd3 varchar2(50);
begin
  ----------------------------------------------------------------------------------------------------------------------------------------------
  -- Articulos
  ----------------------------------------------------------------------------------------------------------------------------------------------  
  -- Buscando Tipopedd para Insercion
  select t.tipopedd into v_tipopedd from operacion.tipopedd t where t.abrev='SOP_GRUPO_ARTICULO';
  -- Insertando los Grupos de Articulo  
  
  ----------------------------------------------------------------------------------------------------------------------------------------------
  -- Necesidad
  ----------------------------------------------------------------------------------------------------------------------------------------------
  select t.tipopedd into v_tipopedd2 from operacion.tipopedd t where t.abrev='SOP_GRUPO_NECESIDAD';
  ---------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
  -- Desactivando Grupo de Compra Actual
  ---------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------  
  select t.tipopedd into v_tipopedd3 from operacion.tipopedd t where t.abrev='SOP_GRUPO_COMPRA';
  UPDATE operacion.opedd
  set estado='0'
  where tipopedd=v_tipopedd3;

update operacion.opedd set codigoc = 1 where idopedd in (4765,4768,5412,5413,5568) and tipopedd = 1064;

  Commit;
end;
